import React, { Component } from 'react';
import axios from 'axios';


class Quote extends Component {
    state = {
        quote: {}
    };

    componentDidMount() {
        alert('data');
        axios.get('https://quotes.rest/qod', { headers: { "Accept": "application/json" }})
            .then(data => {
                alert(data);
                console.log(data);
                this.setState({
                    quote: data.data.contents
                });
            })
            .catch(error => {
                console.log(error);
            }
        )
    }

    render() {
        const quote = this.state.quote ? (
            <div className="row">
                <div className="col s12">
                    <div className="card blue-grey darken-1">
                        <div className="card-content white-text">
                            {/* <span className="card-title">{ this.state.quote.title }</span>
                            <p>{ this.state.quote.quotes[0].quote }</p> */}
                        </div>
                    </div>
                </div>
            </div>
            ) : (
            <div className='center mt-5'>Loading quote...</div>
        );
        return (
            <div className="container">
                <div className="row">
                    <div className="col s12">
                        <br />
                        <h3 className='black-text accent-4s'>Quote of the Day</h3>
                    </div>
                </div>
                { quote }
            </div>
        );
    }
}

export default Quote;
