import React, { Component } from 'react';
import axios from 'axios';

class SaveMemory extends Component {
    state = {                // Not using because of Redux (Central Store)
        post: null
    };

    componentDidMount() {
        let post_id = this.props.match.params.id;
        axios.get('https://jsonplaceholder.typicode.com/posts/' + post_id)
            .then(res => {
                this.setState({
                    post: res.data
                });
            })
    }
    handleClick = (e) => {
        this.props.deletePost(this.props.post.id);
        this.props.history.push('/');
    };
    render() {
        const post = this.state.post ? (
        // const post = this.props.post ? (
            <div className="center">
                <div className="card-content">
                    <h4>{ this.state.post.title }</h4>
                    <p>{ this.state.post.body }</p>
                </div>
            </div>
            ) : (
            <div className='center mt-5'>Loading Post...</div>
        );
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="input-field col s12">
                            <i className="material-icons prefix">account_circle</i>
                            <input id="username" type="text" onChange={this.handleChange} />
                            <label htmlFor="username">Memory Tile:</label>
                        </div>
                        <div className="input-field col s12">
                                <textarea id="icon_prefix2" class="materialize-textarea"></textarea>
                                <label htmlFor="password">Memory Name:</label>
                        </div>
                        <div className="row">
                            <div className="input-field col s12">
                                <button className="btn white-text blue darken-4 pull" type="submit">Submit</button>
                            </div>
                            <a href="/signup">
                                <span className="text-small mt-0">create account</span>
                            </a>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default SaveMemory;
