import React, { Component } from 'react';
import axios from 'axios';

import { server } from '../global';

class Memory extends Component {
    state = {
        memory: {}
    };

    componentDidMount() {
        let memory_id = this.props.match.params.id;
        axios.get(server + '/core/api/memory/' + memory_id, { headers: { "Authorization": "JWT " + localStorage.getItem('token') }})
            .then(data => {
                console.log(data);
                this.setState({
                    memory: data.data
                });
            })
            .catch(error => {
                console.log(error);
            }
        )
    }
    handleClick = (e) => {
        this.props.deletePost(this.props.post.id);
        this.props.history.push('/');
    };
    render() {
        const memory = this.state.memory ? (
            <div class="row">
                <div class="col s12">
                    <div class="card">
                        <div class="card-image">
                            <img src="{}" />
                            <span class="card-title">{ this.state.memory.title }</span>
                            <a class="btn-floating halfway-fab waves-effect waves-light red"><i class="material-icons">add</i></a>
                        </div>
                        <div class="card-content">
                            <p>{ this.state.memory.description }</p>
                        </div>
                    </div>
                </div>
            </div>
            // <div className="center">
            //     <div className="card-content">
            //         <h4>{ this.state.post.title }</h4>
            //         <p>{ this.state.post.body }</p>
            //     </div>
            // </div>
            ) : (
            <div className='center mt-5'>Loading memory...</div>
        );
        return (
            <div className="container">
                { memory }
            </div>
        );
    }
}

export default Memory;
