import React, { Component } from 'react';
import axios from 'axios';

import { server } from '../global';

class SaveMemory extends Component {
    state = {
        title: '',
        description: '',
        image: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        let model = {
            title: this.state.title,
            description: this.state.description,
            image: this.state.image
        };
        axios.post(server + '/core/api/memories', model, { headers: { "Authorization": "JWT " + localStorage.getItem('token') }})
            .then(data => {
                console.log(data);
            })
            .catch(error => {
                console.log(error);
            }
        )
    }

    render() {
        return (
            <div className="container">
                <br />
                <br />
                <h3>Save Your Memory</h3>
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="input-field col s12">
                            <input id="title" type="text" onChange={this.handleChange} />
                            <label htmlFor="title">Memory Title:</label>
                        </div>
                        <div className="input-field col s12">
                            <textarea id="description" className="materialize-textarea" onChange={this.handleChange}></textarea>
                            <label htmlFor="description">Memory Description:</label>
                        </div>
                        <input type="file" />
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                            <button className="btn white-text blue darken-4 pull" type="submit">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default SaveMemory;
