import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import Plane from '../assets/images/plane.png';

class Memories extends Component {
    state = {
        memories: ''
    };

    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then(res => {
                this.setState({
                    memories: res.data
                });
            }
        )
    }

    render() {
        const { memories } = this.state;

        const memoriesList = memories.length ? (
            memories.map(memory => {
                return (
                    <div className="memories card" key={ memory.id }>
                        <img src={ Plane } alt="memory"/>
                        <div className="card-content">
                            <Link to={ '/' + memory.id }>
                                <span className="card-title">{ memory.title }</span>
                            </Link>
                            <p>{ memory.body }</p>
                        </div>
                    </div>
                )
            })
        ) : (
            <p className='center'>Loading Memories...</p>
        );
        return (
            <div className='container home'>
                <h1 className='cyan-text accent-4'>My Memories</h1>
                <div className="row">
                            <div className="input-field col s12">
                                <button className='btn white-text blue darken-4' type="submit">Submit</button>
                            </div>
                        </div>
                { memoriesList }
            </div>
        );
    }
}

export default Memories;
