import React, { Component } from 'react';
import axios from 'axios';

import { server } from '../global';

class Login extends Component {
    state = {
        username: '',
        password: '',
        token: ''
    };

    componentDidMount() {
        axios.get(server + '/token-auth')
            .then(res => {
                this.setState({
                    token: res.data
                });
            }
        )
    }

    render() {
        return (
            <div className='container'>
               <form onSubmit={this.handleSubmit}>
                    <label htmlFor="author">Username:</label>
                    <input className="form-control" id='author' type="text" onChange={this.handleChange} value={this.state.author} />
                    <label htmlFor="content">Password:</label>
                    <input className="form-control" id='content' type="text" onChange={this.handleChange} value={this.state.content} />
                    <button onClick={this.handleSubmit} className='btn white-text darken-4'>Submit</button>
                </form>
            </div>
        );
    }
}

export default Login;
