import React, { Component } from 'react';
import axios from 'axios';

class SaveMemory extends Component {
    state = {
        title: '',
        description: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
    }

    render() {
        return (
            <div className="container">
                <br />
                <br />
                <h3>Save Your Memory</h3>
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="input-field col s12">
                            <input id="username" type="text" onChange={this.handleChange} />
                            <label htmlFor="username">Memory Title:</label>
                        </div>
                        <div className="input-field col s12">
                            <textarea id="icon_prefix2" class="materialize-textarea" onChange={this.handleChange}></textarea>
                            <label htmlFor="password">Memory Description:</label>
                        </div>
                        <div>
                            <div class="file-field input-field">
                                <div class="btn">
                                    <span>File</span>
                                    <input type="file" />
                                </div>
                                <div class="file-path-wrapper">
                                    <input class="file-path validate" type="text" placeholder="Upload image" />
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                            <button className="btn white-text blue darken-4 pull" type="submit">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default SaveMemory;
