import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import { server } from '../global';
import Plane from '../assets/images/memory.jpeg';


class Memories extends Component {
    state = {
        memories: []
    };

    componentDidMount() {
        axios.get(server + '/core/api/memories', { headers: { "Authorization": "JWT " + localStorage.getItem('token') }})
            .then(data => {
                console.log(data);
                this.setState({
                    memories: data.data
                });
            })
            .catch(error => {
                console.log(error);
            }
        )
    }

    render() {
        const { memories } = this.state;

        const memoriesList = memories.length ? (
            memories.map(memory => {
                return (
                    <div class="card horizontal">
                        <div class="card-image">
                            <img src={ Plane } />
                        </div>
                        <div class="card-stacked">
                            <div class="card-content">
                                <p>I am a very simple card. I am good at containing small bits of information.</p>
                            </div>
                            <div class="card-action">
                                <a href="#">This is a link</a>
                            </div>
                        </div>
                    </div>
                    // <div className="memories card" key={ memory.id }>
                    //     {/* <img src={ memory.image } alt="memory"/> */}
                    //     <div className="card-content">
                    //         <img src={ Plane } alt="memory"/>
                    //         <Link to={ 'edit-memory/' + memory.id }>
                    //             <span className="card-title">{ memory.title }</span>
                    //         </Link>
                    //         {
                    //             memory.description.length > 100 ? (
                    //                 <p>{ memory.description.substring(0,100) + '...' }</p>
                    //             ) : (
                    //                 <p>{ memory.description }</p>
                    //             )
                    //         }
                    //     </div>
                    //     <div class="card-action">
                    //         <a href="#">This is a link</a>
                    //     </div>
                    // </div>
                )
            })
        ) : (
            <p className='center'>Loading Memories...</p>
        );
        return (
            <div className='container home'>
                <div className="row">
                    <div className="col s6 push-s6">
                        <h1>
                            <a id="scale-demo" href="/save-memory" className="btn-floating btn-small pull-right">
                                <i className="material-icons">add</i>
                            </a>
                        </h1>
                    </div>
                    <div className="col s6 pull-s6">
                        <h3 className='cyan-text accent-4s'>My Memories</h3>
                    </div>
                </div>
                { memoriesList }
            </div>
        );
    }
}

export default Memories;
