import React, { Component } from 'react';
import axios from 'axios';

import { server } from '../global';

class SaveMemory extends Component {
    state = {
        title: '',
        description: '',
        image: ''
    };

    componentDidMount () {
        let memory_id = this.props.match.params.id;
        axios.get(server + 'api/memory/' + memory_id, { headers: { "Authorization": "JWT " + localStorage.getItem('token') }})
            .then(data => {
                console.log(data);
                this.setState({
                    title: data.data,
                    description: data.data,
                    image: data.data
                });
            })
            .catch(error => {
                console.log(error);
            }
        )
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        let model = {
            title: this.state.title,
            description: this.state.description,
            image: this.state.image
        };
        let memory_id = this.props.match.params.id;
        axios.put(server + 'api/memory/' + memory_id, model, { headers: { "Authorization": "JWT " + localStorage.getItem('token') }})
            .then(data => {
                console.log(data);
                this.setState({
                    title: data.data,
                    description: data.data,
                    image: data.data
                });
            })
            .catch(error => {
                console.log(error);
            }
        )
    }

    render() {
        return (
            <div className="container">
                <br />
                <br />
                <h3>Save Your Memory</h3>
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="input-field col s12">
                            <input id="title" type="text" onChange={this.handleChange} value={this.state.title} />
                            <label htmlFor="title">Memory Title:</label>
                        </div>
                        <div className="input-field col s12">
                            <textarea id="description" className="materialize-textarea" onChange={this.handleChange} value={this.state.description}></textarea>
                            <label htmlFor="description">Memory Description:</label>
                        </div>
                        <input type="file" />
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                            <button className="btn white-text blue darken-4 pull" type="submit">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default SaveMemory;
