import React, { Component } from 'react';
import axios from 'axios';

import { server } from '../global';

class Signup extends Component {
    state = {
        first_name: '',
        last_name: '',
        email: '',
        username: '',
        password: '',
        gender: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let model = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            username: this.state.username,
            password: this.state.password,
            gender: ''
        };
        axios.post(server + '/accounts/api/user/create', model)
            .then(data => {
                console.log(data);
            })
            .catch(error => {
                console.log(error);
            }
        )
    }

    render() {
        return (
            <div className='container'>
                <h1 className='indigo-text darken-4 center-text'>Sign Up</h1>
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="input-field col s4">
                            <input id="first_name" type="text" onChange={this.handleChange} />
                            <label htmlFor="first_name">First Name:</label>
                        </div>
                        <div className="input-field col s4">
                            <input id="last_name" type="text" onChange={this.handleChange} />
                            <label htmlFor="last_name">Last Name:</label>
                        </div>
                        <div className="input-field col s2">
                            <p>
                            <label>
                                <input id="male" class="with-gap" name="group1" type="radio" />
                                <span>Male</span>
                            </label>
                            </p>
                        </div>
                        <div className="input-field col s2">
                            <p>
                            <label>
                                <input id="female" class="with-gap" name="group1" type="radio" />
                                <span>Female</span>
                            </label>
                            </p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                            <input id='username' type="text" onChange={this.handleChange} />
                            <label htmlFor="username">Username:</label>
                        </div>
                        <div className="input-field col s12">
                            <input id='email' type="email" onChange={this.handleChange} />
                            <label htmlFor="email">Email:</label>
                        </div>
                        <div className="input-field col s12">
                            <input id='password' type="password" onChange={this.handleChange} />
                            <label htmlFor="password">Password:</label>
                        </div>
                        <div className="row">
                            <div className="input-field col s12">
                                <button className='btn white-text blue darken-4' type="submit">Submit</button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Signup;
