import React, { Component } from 'react';
import axios from 'axios';

import { server } from '../global';

class Login extends Component {
    state = {
        username: '',
        password: '',
        token: ''
    };

    componentDidMount() {
        const model = {
            username: this.state.username,
            password: this.state.password
        };
        axios.post(server + '/token-auth', model)
            .then(res => {
                this.setState({
                    token: res.data
                });
                localStorage.setItem('token', this.state.token);
            }
        )
    }

    handleChange(e) {
        this.setState({
            [e.target.id]: e.target.value
        });
        console.log(this.state[e.target.id]);
    }

    render() {
        return (
            <div className='container'>
               <form onSubmit={this.handleSubmit}>
                    <label htmlFor="username">Username:</label>
                    <input className="form-control" id='username' type="text" onChange={this.handleChange} />
                    <label htmlFor="password">Password:</label>
                    <input className="form-control" id='password' type="text" onChange={this.handleChange} />
                    <button onClick={this.handleSubmit} className='btn white-text darken-4'>Submit</button>
                </form>
            </div>
        );
    }
}

export default Login;
