import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import { server } from '../global';
import Plane from '../assets/images/memory.jpeg';

class Memory extends Component {
    state = {
        memory: {}
    };

    componentDidMount() {
        let memory_id = this.props.match.params.id;
        axios.get(server + '/core/api/memory/' + memory_id, { headers: { "Authorization": "JWT " + localStorage.getItem('token') }})
            .then(data => {
                console.log(data);
                this.setState({
                    memory: data.data
                });
            })
            .catch(error => {
                console.log(error);
            }
        )
    }
    handleClick = (e) => {
        this.props.deletePost(this.props.post.id);
        this.props.history.push('/');
    };
    render() {
        const memory = this.state.memory ? (
            <div className="row">
                <div className="col s12">
                    <div className="card">
                        <div className="card-image">
                            <img src="{ Plane }" />
                            <span className="card-title">{ this.state.memory.title }</span>
                            <a className="btn-floating halfway-fab waves-effect waves-light red"><i className="material-icons">add</i></a>
                        </div>
                        <div className="card-content">
                            <p>{ this.state.memory.description }</p>
                        </div>
                        <div className="card-action">
                            <i className="red-text material-icons right cursor" onClick={() => {this.deleteMemory(memory.id)}}>delete</i>
                            <Link to={ 'edit-memory/' + memory.id }>
                                <i className="material-icons right">edit</i>
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
            // <div className="center">
            //     <div className="card-content">
            //         <h4>{ this.state.post.title }</h4>
            //         <p>{ this.state.post.body }</p>
            //     </div>
            // </div>
            ) : (
            <div className='center mt-5'>Loading memory...</div>
        );
        return (
            <div className="container">
                { memory }
            </div>
        );
    }
}

export default Memory;
